sys_script_client.script:
function onSubmit() {
   if (g_form.getActionName() != 'execute_now')
       return; 

   if (g_form.getValue('users') != '' || g_form.getValue('roles') != '' || g_form.getValue('ess') == 'true')
       return;

   gsftWarn('Missing required fields', 'You must select at least one User, Role, or Check ESS to generate an Audit set');
   return false;
}

/**
 * A GlideDialogWindow with an OK button
 * @param title
 * @param question
 * @param onPromptComplete (Optional callback function pointer)
 * @return
 */
function gsftWarn(title, question, onPromptComplete) {
  var dialog = new GlideDialogWindow('glide_warn', false);
  dialog.setTitle(title);
  dialog.setPreference('title', question);  
  dialog.setPreference('onPromptComplete', onPromptComplete);
  dialog.render();
}
